import React, { useEffect, useState } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const Dashboard = () => {
    const [tasks, setTasks] = useState([]);
    const [form, setForm] = useState({ title: '', description: '', status: 'Pending', deadline: '' });
    const [filter, setFilter] = useState('');
    const [showForm, setShowForm] = useState(false);

    const token = localStorage.getItem('token');

    // Fetch tasks
    const fetchTasks = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/tasks', {
                headers: { Authorization: token }
            });
            setTasks(res.data);
        } catch (err) {
            toast.error('Failed to fetch tasks');
        }
    };

    useEffect(() => {
        fetchTasks();
    }, []);

    // Add task
    const handleAdd = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/api/tasks', form, {
                headers: { Authorization: token }
            });
            toast.success('Task added!');
            setForm({ title: '', description: '', status: 'Pending', deadline: '' });
            setShowForm(false);
            fetchTasks();
        } catch (err) {
            toast.error('Error adding task');
        }
    };

    // Delete task
    const deleteTask = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/tasks/${id}`, {
                headers: { Authorization: token }
            });
            toast.success('Task deleted!');
            fetchTasks();
        } catch (err) {
            toast.error('Delete failed');
        }
    };

    // Update status
    const updateStatus = async (id, newStatus) => {
        try {
            await axios.patch(`http://localhost:5000/api/tasks/${id}`, { status: newStatus }, {
                headers: { Authorization: token }
            });
            toast.success('Status updated');
            fetchTasks();
        } catch (err) {
            toast.error('Failed to update status');
        }
    };

    // Filtered tasks
    const filteredTasks = filter ? tasks.filter(t => t.status === filter) : tasks;

    return (
        <div className="p-4 max-w-4xl mx-auto">
            <h1 className="text-3xl font-bold mb-4">Task Dashboard</h1>

            {/* Add Task Form Toggle */}
            <button onClick={() => setShowForm(!showForm)} className="bg-blue-500 text-white px-4 py-2 rounded mb-4">
                {showForm ? 'Cancel' : 'Add Task'}
            </button>

            {/* Task Form */}
            {showForm && (
                <form onSubmit={handleAdd} className="bg-white p-4 rounded shadow mb-4">
                    <input type="text" name="title" placeholder="Title" required
                        className="input w-full mb-2" value={form.title} onChange={(e) => setForm({ ...form, title: e.target.value })} />
                    <textarea name="description" placeholder="Description"
                        className="input w-full mb-2" value={form.description} onChange={(e) => setForm({ ...form, description: e.target.value })} />
                    <select name="status" value={form.status} onChange={(e) => setForm({ ...form, status: e.target.value })}
                        className="input w-full mb-2">
                        <option>Pending</option>
                        <option>In Progress</option>
                        <option>Completed</option>
                    </select>
                    <input type="date" name="deadline" value={form.deadline}
                        className="input w-full mb-2" onChange={(e) => setForm({ ...form, deadline: e.target.value })} />
                    <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Save</button>
                </form>
            )}

            {/* Filter Buttons */}
            <div className="mb-4 space-x-2">
                <button onClick={() => setFilter('')} className="px-3 py-1 bg-gray-200 rounded">All</button>
                <button onClick={() => setFilter('Pending')} className="px-3 py-1 bg-yellow-200 rounded">Pending</button>
                <button onClick={() => setFilter('In Progress')} className="px-3 py-1 bg-blue-200 rounded">In Progress</button>
                <button onClick={() => setFilter('Completed')} className="px-3 py-1 bg-green-200 rounded">Completed</button>
            </div>

            {/* Task Cards */}
            {filteredTasks.length === 0 ? (
                <p>No tasks found</p>
            ) : (
                <div className="space-y-3">
                    {filteredTasks.map(task => (
                        <div key={task._id} className="bg-white p-4 shadow rounded">
                            <div className="flex justify-between items-center">
                                <div>
                                    <h3 className="text-lg font-semibold">{task.title}</h3>
                                    <p className="text-sm text-gray-500">{task.description}</p>
                                    <p className="text-xs text-gray-400">Deadline: {task.deadline?.split('T')[0]}</p>
                                </div>
                                <div className="flex gap-2 items-center">
                                    <select value={task.status} onChange={(e) => updateStatus(task._id, e.target.value)}
                                        className="text-sm border rounded px-2 py-1">
                                        <option>Pending</option>
                                        <option>In Progress</option>
                                        <option>Completed</option>
                                    </select>
                                    <button onClick={() => deleteTask(task._id)} className="bg-red-500 text-white text-sm px-2 py-1 rounded">Delete</button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Dashboard;
