import React, { useEffect, useState } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const Dashboard = () => {
    const [tasks, setTasks] = useState([]);
    const [form, setForm] = useState({ title: '', description: '', status: 'Pending', deadline: '' });
    const [filter, setFilter] = useState('');
    const [showForm, setShowForm] = useState(false);
    const token = localStorage.getItem('token');

    const fetchTasks = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/tasks', {
                headers: { Authorization: token }
            });
            setTasks(res.data);
        } catch (err) {
            toast.error('Failed to fetch tasks');
        }
    };

    useEffect(() => {
        fetchTasks();
    }, []);

    const handleAdd = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/api/tasks', form, {
                headers: { Authorization: token }
            });
            toast.success('Task added!');
            setForm({ title: '', description: '', status: 'Pending', deadline: '' });
            setShowForm(false);
            fetchTasks();
        } catch (err) {
            toast.error('Error adding task');
        }
    };

    const deleteTask = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/tasks/${id}`, {
                headers: { Authorization: token }
            });
            toast.success('Task deleted!');
            fetchTasks();
        } catch (err) {
            toast.error('Delete failed');
        }
    };

    const updateStatus = async (id, newStatus) => {
        try {
            await axios.patch(`http://localhost:5000/api/tasks/${id}`, { status: newStatus }, {
                headers: { Authorization: token }
            });
            toast.success('Status updated');
            fetchTasks();
        } catch (err) {
            toast.error('Failed to update status');
        }
    };

    const filteredTasks = filter ? tasks.filter(t => t.status === filter) : tasks;

    const getBadgeClass = (status) => {
        switch (status) {
            case 'Pending': return 'bg-warning text-dark';
            case 'In Progress': return 'bg-info text-dark';
            case 'Completed': return 'bg-success';
            default: return 'bg-secondary';
        }
    };

    return (
        <div className="container my-5">
            <h2 className="text-center mb-4">üìù Task Dashboard</h2>

            <div className="d-flex justify-content-between align-items-center mb-4">
                <button
                    className="btn btn-primary"
                    onClick={() => setShowForm(!showForm)}
                >
                    {showForm ? 'Cancel' : '‚ûï Add Task'}
                </button>

                <div>
                    <button onClick={() => setFilter('')} className="btn btn-outline-secondary btn-sm me-2">All</button>
                    <button onClick={() => setFilter('Pending')} className="btn btn-outline-warning btn-sm me-2">Pending</button>
                    <button onClick={() => setFilter('In Progress')} className="btn btn-outline-info btn-sm me-2">In Progress</button>
                    <button onClick={() => setFilter('Completed')} className="btn btn-outline-success btn-sm">Completed</button>
                </div>
            </div>

            {/* Task Form */}
            {showForm && (
                <div className="card mb-4">
                    <div className="card-body">
                        <form onSubmit={handleAdd}>
                            <div className="mb-3">
                                <label className="form-label">Title</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    required
                                    value={form.title}
                                    onChange={(e) => setForm({ ...form, title: e.target.value })}
                                />
                            </div>
                            <div className="mb-3">
                                <label className="form-label">Description</label>
                                <textarea
                                    className="form-control"
                                    value={form.description}
                                    onChange={(e) => setForm({ ...form, description: e.target.value })}
                                />
                            </div>
                            <div className="mb-3">
                                <label className="form-label">Status</label>
                                <select
                                    className="form-select"
                                    value={form.status}
                                    onChange={(e) => setForm({ ...form, status: e.target.value })}
                                >
                                    <option>Pending</option>
                                    <option>In Progress</option>
                                    <option>Completed</option>
                                </select>
                            </div>
                            <div className="mb-3">
                                <label className="form-label">Deadline</label>
                                <input
                                    type="date"
                                    className="form-control"
                                    value={form.deadline}
                                    onChange={(e) => setForm({ ...form, deadline: e.target.value })}
                                />
                            </div>
                            <button type="submit" className="btn btn-success">Save Task</button>
                        </form>
                    </div>
                </div>
            )}

            {/* Tasks */}
            {filteredTasks.length === 0 ? (
                <p className="text-muted text-center">No tasks found.</p>
            ) : (
                <div className="row g-3">
                    {filteredTasks.map(task => (
                        <div className="col-md-6" key={task._id}>
                            <div className="card shadow-sm">
                                <div className="card-body d-flex justify-content-between">
                                    <div>
                                        <h5 className="card-title mb-1">{task.title}</h5>
                                        <p className="card-text mb-1">{task.description}</p>
                                        <p className="card-text text-muted mb-1">
                                            Deadline: {task.deadline?.split('T')[0] || 'N/A'}
                                        </p>
                                        <span className={`badge ${getBadgeClass(task.status)}`}>
                                            {task.status}
                                        </span>
                                    </div>
                                    <div className="text-end">
                                        <select
                                            value={task.status}
                                            className="form-select form-select-sm mb-2"
                                            onChange={(e) => updateStatus(task._id, e.target.value)}
                                        >
                                            <option>Pending</option>
                                            <option>In Progress</option>
                                            <option>Completed</option>
                                        </select>
                                        <button
                                            className="btn btn-sm btn-danger"
                                            onClick={() => deleteTask(task._id)}
                                        >
                                            Delete
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Dashboard;
